import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./navbar.css";

function Navbar() {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  return (
    <>
      <nav className="navbar">
        <div className="nav-container">
          <NavLink exact to="/" className="nav-logo">
            Logo
          </NavLink>
          <div className="center">
            <div class="wrapper">
                <img className="search-icon" src="data:image/svg+xml;utf8;base64," alt="img" />
                <input className="search" placeholder="Search..." type="text" />
                <img className="clear-icon" src="data:image/svg+xml;utf8;base64," alt="img1"/>
            </div> 
          </div>
           
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <NavLink
                exact
                to="/premium"
                activeClassName="active"
                className="nav-links"
                onClick={handleClick}
              >
                Premuim
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                exact
                to="/movies"
                activeClassName="active"
                className="nav-links"
                onClick={handleClick}
              >
                Movies
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                exact
                to="/register"
                activeClassName="active"
                className="nav-links"
                onClick={handleClick}
              >
                Sign up
              </NavLink>
            </li>
            
            <li className="nav-item">
              <NavLink
                exact
                to="/login"
                activeClassName="active"
                className="nav-links"
                onClick={handleClick}
              >
                sign in
              </NavLink>
            </li>
          </ul>
          <div className="nav-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"}></i>
          </div>
        </div>
      </nav>
    </>
  );
}

export default Navbar;